mapper.select:
SELECT T.*
  FROM (SELECT ROW_NUMBER() OVER(ORDER BY TO_NUMBER(b_id) DESC) AS RNUM,







							B.TITLE LIKE '%' || #{searchText} || '%'


							B.CONT LIKE '%' || #{searchText} || '%' 


							C.NAME = #{searchText}




		) T
 WHERE T.RNUM >= #{fromRowNum} AND T.RNUM <= #{toRowNum}

mapper.update:
UPDATE BBS SET
title =	#{title}
, cont = #{content}
, u_id = #{updId}
, u_id_dttm = sysdate
WHERE b_id = #{b_id}

mapper.insert:
INSERT INTO BBS
(
B_ID
,TITLE
,CONT
,U_ID
,F_ID
,U_ID_DTTM
)
VALUES
(
BID.NEXTVAL
,#{title}
,#{content}
,#{userId}
,#{fid, jdbcType=NUMERIC}
,SYSDATE
)


SELECT FID.NEXTVAL FROM DUAL

	INSERT INTO ADDFILE
	(
	FID
	,FILENAME
	,FILEPATH
	)
	VALUES
	(
	#{fid}
	,#{filename}
	,#{filepath}
	)
